
#!/bin/bash

# NormalDance Comprehensive Testing Script
# This script runs comprehensive tests for NormalDance

set -e

# Configuration
NAMESPACE="production"
CLUSTER_NAME="normaldance-prod"
REGION="us-east-1"
TEST_REPORTS_DIR="test-reports"
COVERAGE_DIR="coverage"
PERFORMANCE_TEST_DURATION="30m"
LOAD_TEST_USERS="1000"
SECURITY_TEST_LEVEL="high"
BROWSER_TEST_DEVICES="chrome,firefox,safari,edge"
MOBILE_TEST_DEVICES="ios,android"

# Colors for output
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
NC='\033[0m' # No Color

# Logging function
log() {
    echo -e "${GREEN}[INFO]${NC} $1"
}

warn() {
    echo -e "${YELLOW}[WARN]${NC} $1"
}

error() {
    echo -e "${RED}[ERROR]${NC} $1"
    exit 1
}

# Check prerequisites
check_prerequisites() {
    log "Checking prerequisites..."
    
    # Check if node is installed
    if ! command -v node &> /dev/null; then
        error "node is not installed"
    fi
    
    # Check if npm is installed
    if ! command -v npm &> /dev/null; then
        error "npm is not installed"
    fi
    
    # Check if jest is installed
    if ! command -v jest &> /dev/null; then
        error "jest is not installed"
    fi
    
    # Check if cypress is installed
    if ! command -v cypress &> /dev/null; then
        error "cypress is not installed"
    fi
    
    # Check if k6 is installed
    if ! command -v k6 &> /dev/null; then
        error "k6 is not installed"
    fi
    
    # Check if docker is installed
    if ! command -v docker &> /dev/null; then
        error "docker is not installed"
    fi
    
    # Check if aws-cli is installed
    if ! command -v aws &> /dev/null; then
        error "aws-cli is not installed"
    fi
    
    log "All prerequisites are installed"
}

# Create test directories
create_test_directories() {
    log "Creating test directories..."
    
    # Create test reports directory
    mkdir -p "$TEST_REPORTS_DIR"
    
    # Create coverage directory
    mkdir -p "$COVERAGE_DIR"
    
    log "Test directories created"
}

# Run unit tests
run_unit_tests() {
    log "Running unit tests..."
    
    # Install dependencies
    npm ci
    
